<?php 	session_start();	require 'NCLNameCaseRu.php';	include 'connect.php';	include '../lang/language.php';	$type = $_GET['t'];	$date_new_post = date("d.m.Y H:i");	$nc = new NCLNameCaseRu();	if ($_SESSION['user_email'] === '' && $_SESSION['user_password'] === '' && $_SESSION['user_view_id'] === '') {		if ($type === 'load') {			?>				<h2 class="no_posts">Для просмотра записей, войдите в аккаунт</h2>			<?php		}	} else {		$email = $_SESSION['user_email'];		$password = $_SESSION['user_password'];		$user_id_get = intval($_SESSION['user_view_id']);		$check_user = mysqli_query($connect, "SELECT * FROM `users` WHERE `email` = '$email' AND `password` = '$password'");		if (mysqli_num_rows($check_user) > 0) {			$user = mysqli_fetch_assoc($check_user);			$my_id_get = intval($user['id']);			if ($type === 'load') {				$res = mysqli_query($connect, "SELECT * FROM `user_posts` WHERE `publishing` = '$user_id_get' ORDER BY date DESC");				//Выводим все сообщения на экран				while($row = mysqli_fetch_assoc($res)) { 					$creator = intval($row['publishing']);					$creator_post = intval($row['creator']);					$check_user_post = mysqli_query($connect, "SELECT * FROM `users` WHERE `id` = '$creator'");					$check_user_creator_post = mysqli_query($connect, "SELECT * FROM `users` WHERE `id` = '$creator_post'");					if (mysqli_num_rows($check_user_post) > 0) {						$user_post = mysqli_fetch_assoc($check_user_post);						?>							<div class="contentData">								<div class="postItem">									<div class="userPostItem">										<?php											if ($user_post['avatar'] == '') {										?>												<img draggable="false" oncontextmenu="return false" onclick="window.location = 'profile.php?id=<?php echo(intval($user_post['id'])); ?>'" class="avatarUserPost" src="<?php echo($url_no_avatar); ?>">										<?php											} else {										?>												<img draggable="false" oncontextmenu="return false" onclick="window.location = 'profile.php?id=<?php echo(intval($user_post['id'])); ?>'" class="avatarUserPost" src="<?php echo($user_post['avatar']); ?>">										<?php											}										?>										<div class="nameUserPostItem">											<div class="divPostUserName">												<h2 class="postUserName"><?php echo($user_post['first_name'] . ' ' . $user_post['last_name']); ?><?php if ($user_post['verification'] == 1) { echo('<img draggable="false" oncontextmenu="return false" class="imgVerificationPost" src="' . $url_verification_img . '">'); } ?></h2>												<div class="dataPostItem">																									</div>												<div class="tooltipMenu menu_post">													<i class="menuPost"><img class="menuPost" src="assets/icons/ic_menu_post.png"></i>													<span class="tooltipTextMenu">														<ul class="listMenuPost">															<?php if (intval($row['publishing']) == $my_id_get) { ?>															<li onclick="window.location = 'vendor/user_posts.php?t=remove&post_id=<?php echo(intval($row['id'])); ?>'" class="itemMenuPost"><?php echo($string_delete_post); ?></li>															<?php } ?>															<?php if (intval($row['publishing']) != $my_id_get) { ?>															<li onclick="window.location = 'vendor/user_posts.php?t=repost&post_id=<?php echo(intval($row['id'])); ?>'" class="itemMenuPost"><?php echo($string_repost); ?></li>															<?php } ?>														</ul>													</span>												</div>											</div>											<div class="divPostDate">												<h2 class="postDate"><?php echo($row['date']); ?></h2>												<img class="postDevice" draggable="false" title="<?php if ($row_users_posts['device'] == 'pc') { echo($string_public_pс); } if ($row_users_posts['device'] == 'phone') { echo($string_public_phone); } ?>" oncontextmenu="return false" src="<?php echo('assets/icons/ic_' . $row['device'] . '.png'); ?>">											</div>										</div>									</div>									<div class="postItemContent">										<?php if ($row['message'] != '') { ?>											<h2 class="postMessage"><?php echo htmlspecialchars($row['message']); ?></h2>										<?php } ?>										<?php if ($row['image'] != '') { ?>											<img <?php if ($row['message'] != '') { echo('style="margin-top: 14px;"'); }?> draggable="false" oncontextmenu="return false" class="imgPostMessage" src="<?php echo strip_tags($row['image']); ?>">										<?php } ?>									</div>									<div class="typePostItems">										<?php if ($row['repost'] == 1) { ?> <h2 class="postUserRepost"><?php echo($user_post['first_name'] . ', '); if ($user_post['sex'] == 'f') { echo($string_repost_f); } else { echo($string_repost_m); } echo(' <a class="repost" href="profile.php?id=' . intval($user_creator_post['id']) . '">' . $nc->q($user_creator_post['first_name'], 1) . '</a>'); ?></h2> <?php } ?>										<?php if ($row['type'] == 'ads') { ?> <h2 class="postUserRepost"><?php echo($string_message_post_ads); ?></h2> <?php } ?>									</div>									<?php if ($row['type'] != 'ads') { ?>									<div class="divPostButtons">										<div class="divPostItemButton" onclick="postLike(<?php echo($row['id']); ?>)">											<?php $post_id_liked = intval($row['id']); $check_post_liked = mysqli_query($connect, "SELECT * FROM `post_liked` WHERE `post` = '$post_id_liked'"); $check_post_my_liked = mysqli_query($connect, "SELECT * FROM `post_liked` WHERE `post` = '$post_id_liked' AND `creator` = '$my_id_get'"); ?>											<img draggable="false" oncontextmenu="return false" src="<?php if (mysqli_num_rows($check_post_my_liked) > 0) { ?>assets/icons/ic_liked_v2.png<?php } else { ?>assets/icons/ic_like_v2.png<?php } ?>" class="postItemButton">											<h2 class="postItemBottonContent"><?php echo(mysqli_num_rows($check_post_liked)); ?></h2>										</div>									</div>									<?php } ?>								</div>							</div>						<?php					}				}			}			if ($type === 'send') {				$mess = mysqli_real_escape_string($connect, $_POST['mess']);				$image = mysqli_real_escape_string($connect, $_POST['image']);				$device = $_SESSION['type_device'];				//Проверям есть ли переменные на добавление				if ($my_id_get === intval($user['id'])) {					if (isset($_POST['mess']) && $_POST['mess'] != "" && $_POST['mess'] != " ") {						$res = mysqli_query($connect, "INSERT INTO `user_posts` (`publishing`,`message`,`creator`,`device`,`image`) VALUES ('$user_id_get','$mess','$my_id_get','$device','$image')");					} else {						if ($_POST['image'] != "") {							$res = mysqli_query($connect, "INSERT INTO `user_posts` (`publishing`,`message`,`creator`,`device`,`image`) VALUES ('$user_id_get','$mess','$my_id_get','$device','$image')");						}					}				}			}			if ($type === 'remove') {				$post_id = intval($_GET['post_id']);				$sql_remove = mysqli_query($connect ,"DELETE FROM `user_posts` WHERE `id` = '$post_id' AND `publishing` = '$my_id_get'");				if ($sql_remove) {					?>						<script type="text/javascript">							window.history.go(-1);						</script>					<?php				}			}			if ($type === 'repost') {				$post_id = intval($_GET['post_id']);				$check_post = mysqli_query($connect, "SELECT * FROM `user_posts` WHERE `id` = '$post_id'");				if (mysqli_num_rows($check_post) > 0) {					$post = mysqli_fetch_assoc($check_post);					$post_creator = intval($post['creator']);					$post_id = intval($post['id']);					$post_publishing = intval($post['publishing']);					$post_message = $post['message'];					$post_device = $post['device'];					$post_image = $post['image'];					$post_date = $post['date'];					$res = mysqli_query($connect, "INSERT INTO `user_posts` (`publishing`,`message`,`creator`,`device`,`image`,`repost`,`original_post`) VALUES ('$my_id_get','$post_message','$post_creator','$post_device','$post_image','1','$post_id')");					if ($res) {						?><script type="text/javascript">window.history.go(-1);</script><?php					}				}			}			if ($type == 'like') {				$post_like_id = intval($_GET['post']);				$check_post_liked = mysqli_query($connect, "SELECT * FROM `post_liked` WHERE `post` = '$post_like_id' AND `creator` = '$my_id_get'");				if (mysqli_num_rows($check_post_liked) > 0) {					mysqli_query($connect ,"DELETE FROM `post_liked` WHERE `post` = '$post_like_id' AND `creator` = '$my_id_get'");				} else {					mysqli_query($connect, "INSERT INTO `post_liked` (`creator`,`post`) VALUES ('$my_id_get','$post_like_id')");				}			}		}	}?>